mapper.update:
update t_place as tp set
    tp.place_name=#{placeName},
    tp.place_type=#{placeType},
    tp.update_by=#{updateBy},
    tp.update_time=#{updateTime}
where tp.place_id=#{placeId}

update t_place as tp set
    tp.is_eff=#{isEff},
    tp.update_by=#{updateBy},
    tp.update_time=#{updateTime}
where tp.place_id=#{placeId}
